import React, { useState } from "react";
import { getLibroPorId } from "./tuArchivoDeServicios"; // Cambia esta ruta a donde tienes tu función

export default function BuscarLibroPorId() {
  const [id, setId] = useState("");
  const [libro, setLibro] = useState(null);
  const [error, setError] = useState("");

  const esGuidValido = (id) => {
    const regexGuid = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
    return regexGuid.test(id);
  };

  const manejarBusqueda = async () => {
    setError("");
    setLibro(null);

    if (!id) {
      setError("Por favor, ingresa un ID.");
      return;
    }

    if (!esGuidValido(id)) {
      setError("El ID debe tener formato GUID válido.");
      return;
    }

    try {
      const resultado = await getLibroPorId(id);
      setLibro(resultado);
    } catch (err) {
      setError("No se encontró un libro con ese ID.");
    }
  };

  return (
    <div>
      <h2>Buscar libro por ID</h2>
      <input
        type="text"
        placeholder="Ingresa ID del libro"
        value={id}
        onChange={(e) => setId(e.target.value)}
      />
      <button onClick={manejarBusqueda}>Buscar</button>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {libro && (
        <div style={{ marginTop: "1em" }}>
          <h3>{libro.titulo}</h3>
          <p><b>Autor:</b> {libro.autorLibro}</p>
          <p><b>Fecha de publicación:</b> {libro.fechaPublicacion || "N/A"}</p>
          {/* Muestra más campos si quieres */}
        </div>
      )}
    </div>
  );
}
