import React, { useEffect, useState } from "react";
import { getLibros, eliminarLibro } from "../api/libreriaMaterialService";

export default function LibreriaMaterialList({ onEdit }) {
  const [libros, setLibros] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    cargarLibros();
  }, []);

  async function cargarLibros() {
    try {
      const response = await getLibros();
      setLibros(response.data);
    } catch (e) {
      setError(e.message);
    }
  }

  async function handleEliminar(id) {
    if (!window.confirm("¿Seguro que quieres eliminar este libro?")) return;
    try {
      await eliminarLibro(id);
      cargarLibros();
    } catch (e) {
      setError(e.message);
    }
  }

  if (error) return <p style={{color:"red"}}>{error}</p>;

  if (libros.length === 0) return <p>No hay libros para mostrar.</p>;

  return (
    <table border="1" cellPadding="10" style={{ borderCollapse: "collapse" }}>
      <thead>
        <tr>
          <th>Título</th>
          <th>Autor</th>
          <th>Fecha Publicación</th>
          <th>Acciones</th>
        </tr>
      </thead>
      <tbody>
        {libros.map((libro) => (
          <tr key={libro.libreriaMateriaId}>
            <td>{libro.titulo}</td>
            <td>{libro.autorLibro}</td>
            <td>{new Date(libro.fechaPublicacion).toLocaleDateString()}</td>
            <td>
              <button onClick={() => onEdit(libro)}>Editar</button>{" "}
              <button onClick={() => handleEliminar(libro.libreriaMateriaId)}>
                Eliminar
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
