import React, { useState } from "react";
import LibreriaMaterialList from "./LibreriaMaterialList";
import LibreriaMaterialForm from "./LibreriaMaterialForm";

export default function LibreriaMaterialView() {
  const [libroSeleccionado, setLibroSeleccionado] = useState(null);
  const [modoEdicion, setModoEdicion] = useState(false);

  function handleEdit(libro) {
    setLibroSeleccionado(libro);
    setModoEdicion(true);
  }

  function handleSave(libroActualizado) {
    // Aquí pondrías la lógica para actualizar o crear, según si libroActualizado tiene id
    // Por ahora solo cerramos el modo edición:
    setModoEdicion(false);
    setLibroSeleccionado(null);
    // También deberías recargar la lista si haces cambios
  }

  function handleCancel() {
    setModoEdicion(false);
    setLibroSeleccionado(null);
  }

  return (
    <div>
      {modoEdicion ? (
        <LibreriaMaterialForm
          libro={libroSeleccionado}
          onSave={handleSave}
          onCancel={handleCancel}
        />
      ) : (
        <LibreriaMaterialList onEdit={handleEdit} />
      )}
    </div>
  );
}
