import React, { useEffect, useState } from "react";
import { getLibros, eliminarLibro } from "../api/libreriaMaterialService";

export default function LibreriaMaterialList({ onEdit, onDelete }) {
  const [libros, setLibros] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    cargarLibros();
  }, []);

  async function cargarLibros() {
    try {
      const response = await getLibros();

      // Aquí asumimos que response.data es el arreglo de libros
      // Si tu API devuelve la lista en otro campo, cámbialo acá
      const librosData = Array.isArray(response.data) ? response.data : [];

      setLibros(librosData);
      setError(null);
    } catch (e) {
      setError(e.message);
      setLibros([]);
    }
  }

  async function handleEliminar(id) {
    if (!window.confirm("¿Seguro que quieres eliminar este libro?")) return;
    try {
      await eliminarLibro(id);
      await cargarLibros();
    } catch (e) {
      setError(e.message);
    }
  }

  if (error) return <p style={{ color: "red" }}>{error}</p>;

  if (!Array.isArray(libros) || libros.length === 0)
    return <p>No hay libros para mostrar.</p>;

  return (
    <table border="1" cellPadding="10" style={{ borderCollapse: "collapse" }}>
      <thead>
        <tr>
          <th>Título</th>
          <th>Autor</th>
          <th>Fecha Publicación</th>
          <th>Acciones</th>
        </tr>
      </thead>
      <tbody>
        {libros.map((libro) => (
          <tr key={libro.libreriaMaterialId}>
            <td>{libro.titulo}</td>
            <td>{libro.autorLibro}</td>
            <td>
              {libro.fechaPublicacion
                ? new Date(libro.fechaPublicacion).toLocaleDateString()
                : ""}
            </td>
            <td>
              <button onClick={() => onEdit(libro)}>Editar</button>{" "}
              <button onClick={() => handleEliminar(libro.libreriaMaterialId)}>
                Eliminar
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
