import React, { useState, useEffect } from "react";

export default function LibreriaMaterialForm({ libro, onSave, onCancel }) {
  const [titulo, setTitulo] = useState("");
  const [autor, setAutor] = useState("");
  const [fechaPublicacion, setFechaPublicacion] = useState("");

  useEffect(() => {
    if (libro) {
      setTitulo(libro.titulo || "");
      setAutor(libro.autorLibro || "");
      setFechaPublicacion(
        libro.fechaPublicacion
          ? new Date(libro.fechaPublicacion).toISOString().substring(0, 10)
          : ""
      );
    } else {
      setTitulo("");
      setAutor("");
      setFechaPublicacion("");
    }
  }, [libro]);

  function handleSubmit(e) {
    e.preventDefault();
    onSave({
      ...libro,
      titulo,
      autorLibro: autor,  // Importante: coincide con la propiedad del backend
      fechaPublicacion: fechaPublicacion
        ? new Date(fechaPublicacion).toISOString()
        : null,
    });
  }

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>
          Título:
          <input
            type="text"
            value={titulo}
            onChange={(e) => setTitulo(e.target.value)}
            required
          />
        </label>
      </div>

      <div>
        <label>
          Autor:
          <input
            type="text"
            value={autor}
            onChange={(e) => setAutor(e.target.value)}
            required
          />
        </label>
      </div>

      <div>
        <label>
          Fecha de Publicación:
          <input
            type="date"
            value={fechaPublicacion}
            onChange={(e) => setFechaPublicacion(e.target.value)}
          />
        </label>
      </div>

      <button type="submit">Guardar</button>{" "}
      <button type="button" onClick={onCancel}>
        Cancelar
      </button>
    </form>
  );
}
