import React, { useEffect, useState } from "react";
import { getLibros, eliminarLibro } from "../api/libreriaMaterialService";
import "../styles.css";

export default function LibreriaMaterialList({ onEdit, onDelete }) {
  const [libros, setLibros] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    cargarLibros();
  }, []);

  async function cargarLibros() {
    try {
      const response = await getLibros();
      // Aquí response es directamente un array
      const librosData = Array.isArray(response) ? response : [];
      setLibros(librosData);
      setError(null);
    } catch (e) {
      setError(e.message);
      setLibros([]);
    }
  }

  async function handleEliminar(id) {
    if (!window.confirm("¿Seguro que quieres eliminar este libro?")) return;
    try {
      await eliminarLibro(id);
      await cargarLibros();
    } catch (e) {
      setError(e.message);
    }
  }

  if (error) return <p style={{ color: "red" }}>{error}</p>;

  if (!Array.isArray(libros) || libros.length === 0)
    return <p>No hay libros para mostrar.</p>;

  return (
    <table className="tabla-libros">
      <thead>
        <tr>
          <th>Título</th>
          <th>Autor (ID)</th>
          <th>Fecha Publicación</th>
          <th>Acciones</th>
        </tr>
      </thead>
      <tbody>
        {libros.map((libro) => (
          <tr key={libro.id}>
            <td>{libro.titulo}</td>
            <td>{libro.autorId || "Sin autor"}</td>
            <td>
              {libro.fechaPublicacion
                ? new Date(libro.fechaPublicacion).toLocaleDateString()
                : ""}
            </td>
            <td>
              <button
                className="btn-accion btn-editar"
                onClick={() => onEdit(libro)}
              >
                Editar
              </button>
              <button
                className="btn-accion btn-eliminar"
                onClick={() => handleEliminar(libro.id)}
              >
                Eliminar
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
