const API_URL = "https://localhost:7155/api/LibreriaMaterial"; // Ajusta el puerto si es diferente

export async function getLibros() {
  const res = await fetch(API_URL);
  if (!res.ok) throw new Error("Error al obtener libros");
  return await res.json();
}

export async function getLibro(id) {
  const res = await fetch(`${API_URL}/${id}`);
  if (!res.ok) throw new Error("Libro no encontrado");
  return await res.json();
}

export async function crearLibro(libro) {
  const res = await fetch(API_URL, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(libro),
  });
  if (!res.ok) throw new Error("Error al crear libro");
  return await res.json();
}

export async function actualizarLibro(id, libro) {
  const res = await fetch(`${API_URL}/${id}`, {
    method: "PUT",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(libro),
  });
  if (!res.ok) throw new Error("Error al actualizar libro");
  return await res.json();
}

export async function eliminarLibro(id) {
  const res = await fetch(`${API_URL}/${id}`, { method: "DELETE" });
  if (!res.ok) throw new Error("Error al eliminar libro");
  return await res.json();
}
